import { CREATE_NEW_USER } from '../actions/Types';

const authReducer = (state = initialState, action) => {
	switch (action.type) {
		case CREATE_NEW_USER:
			return {
				...state,
				user: action.payload
			};

		default:
			return state;
	}
};

export default authReducer;
