import React, { Component } from 'react'
import { fetchSchema } from "../redux/actions/schemaAction"
import { connect } from 'react-redux'
import { HomeButton } from './HomeButton'


class Itynerary extends Component {

    state = {
        id: null
    }



    componentDidMount() {
        console.log(this.props)

        let id = this.props.match.params.Itynerary_id
        this.setState({
            id: id
        })
        this.props.getSchema()
    }


    render() {
        // console.log(this.props)
        const { schema } = this.props
        const ItyneraryList = schema.length ? (schema.map(schem => {
            return (
                <div key={schem._id}>
                    <h5>{schem.title}</h5>
                    <p>{schem.hashtag}</p>
                    <p>{schem.rating}</p>
                    <img src={schem.profilePic} alt="schemaImg" className="cityimages"></img>
                    <p>{schem.duration}</p>
                    <p>With just {schem.price} euros</p>
                </div>
            )
        })) : (
                <div className="center">Getting Schema</div>
            )
        return (
            <div>
                <h6 className="center">Your best Itynerary</h6>
                {ItyneraryList}
                <HomeButton />
                <h3>{this.state.id}</h3>

            </div>
        )
    }
}
const mapStateToProps = (state) => {
    return {

        schema: state.schemaReducer.schema

    }


}

const mapDispatchToProps = (dispatch) => {
    return {
        getSchema: () => dispatch(fetchSchema())
    }
}

export default connect(mapStateToProps, mapDispatchToProps)(Itynerary);
