import React, { Component } from 'react';
import { connect } from 'react-redux';
import { Link } from 'react-router-dom';
import { HomeButton } from './HomeButton';

export class Activities extends Component {
   render() {
      const schem = this.props.itinerary;
      // const { schema } = this.props;

      const ItyneraryList = schem.length ? (
         schem.forEach(schema => {
            return (
               <div key={schema._id} className="row">
                  <div className="col s12 16">
                     <div className="card">
                        <div className="card-image">
                           <img src={schema.images} alt="schemaImg" />
                           <a
                              href="#"
                              className="halfway-fab btn-floating orange pulse">
                              <i className="material-icons">favorite</i>
                           </a>
                        </div>

                        <div className="card-content">
                           <span className="card-title">{schema.title}</span>

                           <p className="flow-text">{schema.duration}.</p>
                           <p className="flow-text">{schema.hashtag}</p>
                           <p className="flow-text">
                              People rate this activity with {schem.rating}.
                           </p>
                           <p className="flow-text">
                              With just {schema.price} euros for the best
                              journey !{' '}
                           </p>
                        </div>
                        <div className="card-action"></div>
                     </div>
                  </div>
               </div>
            );
         })
      ) : (
         <div className="center">Loading...</div>
      );

      return (
         <div>
            <h5 className="center  z-depth-1">
               {' '}
               Find here your best activities
            </h5>
            <div>{ItyneraryList}</div>

            <div className="mid-container" />
            <div className="footer-container">
               <HomeButton />
            </div>
         </div>
      );
   }
}
const mapStateToProps = state => {
   return {
      itinerary: state.schemaReducer.selectedSchema
   };
};

export default connect(mapStateToProps)(Activities);
