import React, { Component } from 'react'
import { HomeButton } from './HomeButton'
import { connect } from 'react-redux';
import { fetchCities } from "../redux/actions/cityAction"


function searchingFor(term) {
    return function (x) {
        return x.name.toLowerCase().includes(term.toLowerCase()) || !term
    }
}

class MyCities extends Component {
    constructor(props) {
        super(props)
        this.state = {
            cities: '',
            term: ''
        }

        this.searchHandler = this.searchHandler.bind(this)
    }
    searchHandler(e) {
        this.setState({
            term: e.target.value
        })
    }

    componentDidMount() {
        this.props.getCities()
    }

    render() {
        console.log(this.props)
        const { cities } = this.props

    let citiesList=  cities.filter(searchingFor(term)).map(city =>
            <div key={city._id}>
        //             <li> {city.name} </li>
        //             <img src={city.images} alt="cityimages"></img>
        //         </div>
            )
        // const citiesList = cities.length ? (cities.map(city => {

        //     return (
        //         <div key={city._id}>
        //             <li> {city.name} </li>
        //             <img src={city.images} alt="cityimages"></img>
        //         </div>
        //     )
        // })) : (
        //         <div>Fetching Cities..</div>
        //     )

        return (


            <div>
                <form>
                    <input type="text" onChange={this.searchHandler}></input>
                </form>
                <div >
                    {citiesList}
                </div>
                <div className="footer-container">
                    <HomeButton />
                </div>
            </div>

        )
    }
}

const mapStateToProps = (state) => {
    return {

        cities: state.cities
    }
}

const mapDispatchToProps = (dispatch) => {
    return {
        getCities: () => dispatch(fetchCities())
    }
}



export default connect(mapStateToProps, mapDispatchToProps)(MyCities);
