import React, { Component } from "react";
import { connect } from "react-redux";

export class Activities extends Component {
  render() {
    const schem = this.props.itinerary;
    //  console.log(schem);
    let images = schem.map(image => {
      return <img src={image.images} />;
    });
    return (
      <div className="card-content">
        {/* <span className="card-title">{schem.title}</span> */}
        <p className="flow-text">{schem.hashtag}</p>
        <p className="flow-text">People rate this activity with.</p>
        <img src={images} />
        <p className="flow-text">With just euros for the best journey ! </p>
      </div>
    );
  }
}

const mapStateToProps = state => {
  return {
    itinerary: state.schemaReducer.selectedSchema
  };
};

export default connect(mapStateToProps)(Activities);
