import React, { Component } from 'react'
import { fetchSchema } from "../redux/actions/schemaAction"
import { connect } from 'react-redux'
import { HomeButton } from './HomeButton'


class Itynerary extends Component {
    state = {
        id: null
    }
    componentDidMount() {
        console.log(this.props)
        let id = this.props.match.params.paraid;
        this.setState({
            id: id
        })


        this.props.getSchema()
    }


    render() {
        let { schema } = this.props

        schema = this.state.schema ? (
            <div>{schema.title}</div>) : (
                <div>LOADING</div>
            )
        // const ItyneraryList = this.state.schema ? (schema.map(schem => {
        //     return (
        //         <div key={schem.CityID}>
        //             <h5>{schem.title}</h5>
        //             <p>{schem.hashtag}</p>
        //             <p>{schem.rating}</p>
        //             <img src={schem.profilePic} alt="schemaImg" className="cityimages"></img>
        //             <p>{schem.duration}</p>
        //             <p>{schem.CityID}</p>
        //             <p>With just {schem.price} euros</p>
        //         </div>
        //     )
        // })) : (
        //         <div className="center">Getting Schema</div>
        //     )
        return (
            <div>
                <h6 className="center">{this.state.id}</h6>

                {schema}
                {/* {ItyneraryList} */}
                <HomeButton />

            </div>
        )
    }
}
const mapStateToProps = (state) => {
    return {

        schema: state.schemaReducer.schema

    }


}

const mapDispatchToProps = (dispatch) => {
    return {
        getSchema: () => dispatch(fetchSchema())
    }
}

export default connect(mapStateToProps, mapDispatchToProps)(Itynerary);
