import React, { Component } from "react";
import { HomeButton } from "./HomeButton";
import { connect } from "react-redux";
import { fetchSchema } from "../redux/actions/schemaAction";

import { Link } from "react-router-dom";

class activities extends Component {
  searchHandler = e => {
    this.setState({
      word: e.target.value
    });
  };

  componentDidMount() {
    this.props.getCities();
  }

  render() {
    console.log(this.props);
    const { cities } = this.props;
    const citiesList = cities.length ? (
      cities.map(city => {
        return (
          <div key={city._id} className="card center ">
            <h3 className="orange"> {city.name} </h3>
            <Link to={"/" + city.name}>
              <img src={city.images} className="cityimages" alt="cityimages" />
            </Link>
          </div>
        );
      })
    ) : (
      <div className="center">Fetching Cities..</div>
    );

    return (
      <div>
        <form className="center container">
          <label htmlFor="SearchBar">Search your city</label>
          <input type="text" id="SearchBar" onChange={this.searchHandler} />
        </form>
        <div className=" card-content">{citiesList}</div>

        <div className="footer-container">
          <HomeButton />
        </div>
      </div>
    );
  }
}

const mapStateToProps = state => {
  return {
    schema: state.schemaReducer.schema
  };
};

const mapDispatchToProps = dispatch => {
  return {
    getCities: () => dispatch(fetchSchema())
  };
};

export default connect(
  mapStateToProps,
  mapDispatchToProps
)(activities);
