import cityReducer from './reducers/cityReducer';

import thunk from 'redux-thunk';

import { createStore, applyMiddleware } from 'redux';



export default createStore(cityReducer, () => (applyMiddleware(thunk), window.__REDUX_DEVTOOLS_EXTENSION__ && window.__REDUX_DEVTOOLS_EXTENSION__()))
