import React, { Component } from 'react';
import { connect } from 'react-redux';
import { Link } from 'react-router-dom';
import { HomeButton } from './HomeButton';

export class Activities extends Component {
   render() {
      const schem = this.props.itinerary;
      console.log(schem);

      const ItyneraryList = schem.length ? (
         schem.map(schema => {
            return (
               <div>
                  <div className="card-image">
                     <img src={schema.images} alt="schemaImg" />
                  </div>

                  <div className="card-content">
                     <span className="card-title">{schema.title}</span>

                     <p className="flow-text">{schema.duration}.</p>
                     <p className="flow-text">{schema.hashtag}</p>
                     <p className="flow-text">
                        People rate this activity with {schem.rating}.
                     </p>
                     <p className="flow-text">
                        With just {schema.price} euros for the best journey !{' '}
                     </p>
                  </div>
               </div>
            );
         })
      ) : (
         <div className="center">Loading...</div>
      );

      return (
         <div>
            <div>{ItyneraryList}</div>

            <HomeButton />
         </div>
      );
   }
}
const mapStateToProps = state => {
   return {
      itinerary: state.schemaReducer.selectedSchema
   };
};

export default connect(mapStateToProps)(Activities);
