import React, { Component } from "react";
import { connect } from "react-redux";

export class Activities extends Component {
  render() {
    const images = [
      "http://placehold.it/100x100/76BD22",
      "http://placehold.it/100x100/76BD23"
    ];

    //  const d = images.map(element => {
    //    return <img src={element.images} />;
    //  });
    <ul className="product-gallery-thumbs__list">
      {images.map(function(imageSrc) {
        return (
          <li key={imgSrc}>
            <img src={imgSrc} />
          </li>
        );
      })}
    </ul>;
    const schem = this.props.itinerary;

    return (
      <div className="card-content">
        <span className="card-title">{schem.title}</span>
        <p className="flow-text">{schem.hashtag}</p>
        <p className="flow-text">
          People rate this activity with {schem.rating}.
        </p>

        <img src={this.props.d} />
        <p className="flow-text">
          With just {schem.price} euros for the best journey !{" "}
        </p>
      </div>
    );
  }
}

const mapStateToProps = state => {
  return {
    itinerary: state.schemaReducer.selectedSchema
  };
};

export default connect(mapStateToProps)(Activities);
